mapper.insert:

	SELECT COUNT(apprelmnt_no) + 1
	FROM appr_elmnt

INSERT INTO appr_elmnt(apprelmnt_no
	, appr_result
	, appr_reason
	, appr_date
	, approver
	, dept_no
	, appr_no
)
VALUES(#{apprelmntNo}
	, 'a1'
	, ''
	, null
	, #{approver}
	, #{deptNo}
	, #{apprNo}
)

mapper.update:
UPDATE appr_elmnt
SET appr_result = #{apprResult}
	, appr_reason = #{apprReason}
	, appr_date = SYSDATE
	, sign_no = #{signNo}
WHERE approver = #{approver}
	AND appr_no = #{apprNo}
