mapper.select:
SELECT approval.appr_no , form_name , appr_title , user_name , department_name , create_date FROM approval JOIN appr_form ON approval.appr_type=appr_form.appr_type JOIN employee ON approval.user_no=employee.user_no LEFT JOIN department ON approval.dept_no=department.department_id
JOIN appr_elmnt ON approval.appr_no = appr_elmnt.appr_no
JOIN reference ON approval.appr_no = reference.appr_no
WHERE appr_status = #{apprStatus}
AND approval.user_no = #{userNo}
AND approver = #{userNo}
AND reference.user_no = #{userNo}

mapper.insert:


    SELECT 'YD' || LPAD(NVL(MAX(TO_NUMBER(SUBSTR(appr_no, 3))), 0) + 1, 3, '0')
    FROM approval

INSERT INTO approval
VALUES(#{apprNo}
	, #{apprTitle}
	, #{apprContent}
	, SYSDATE
	, SYSDATE + INTERVAL '1' YEAR
	, 'a1'
	, #{deptNo}
	, null
	, #{reportNo}
	, #{userNo}
	, #{reserNo}
	, #{apprType}
)

mapper.delete:
DELETE FROM approval
WHERE appr_no = #{apprNo}
	and user_no = #{userNo}
	AND appr_status = 'a1'
