mapper.insert:


	SELECT contr_no_seq.NEXTVAL
	FROM dual

	INSERT INTO contracts(
      contr_no
      ,vend_code
      ,contr_title
      ,contr_content
      ,contr_type
      ,create_date
      ,contr_status
      ,contr_A
      ,contr_B
      ,sign_date_A
      ,sign_date_B
      ,contr_start
      ,contr_end
      ,SIGN_IMAGE
      ,cntr_location
      ,cntr_money
		)
	VALUES (
	  #{contrNo}
      ,#{vendCode}
      ,#{contrTitle}
      ,#{contrContent}
      ,#{contrType}
      ,#{createDate}
      ,#{contrStatus}
      ,#{contrA}
      ,#{contrB}
      ,#{signDateA}
      ,#{signDateB}
      ,#{contrStart}
      ,#{contrEnd}
      ,#{signImage, jdbcType=BLOB}
      ,#{cntrLocation}
      ,#{cntrMoney}
	)
