mapper.insert:

        SELECT REPORT_NO_SEQ.NEXTVAL FROM DUAL

		INSERT INTO REPORT
				( REPORT_NO
			    , REPORT_TITLE
			    , WRITE_DATE
			    , USER_NO
			    , TEAM_NO
			    , TRANS_START
			    , TRANS_END
			    , REPORT_STATUS ) 

		VALUES ( #{reportNo}
		        , #{reportTitle}
		        , SYSDATE
		        , #{userNo}
		        , #{teamNo}
		        , #{transStart}
		        , #{transEnd} 
		        , '결재 중')

INSERT INTO TRANS_HISTORY
            ( TRANS_ID
            , TRANS_TYPE
            , TRANS_DATE 
            , WITHDRAWAL
            , DEPOSIT
            , BALANCE
            , PURPOSE_USE
            , REPORT_NO
            ) 
VALUES ( TRANS_ID_SEQ.NEXTVAL
        , #{transType}
        , #{transDate}
        , #{withdrawal}
        , #{deposit}
        , #{balance}
        , #{purposeUse}
        , #{reportNo}
        )

INSERT INTO CORCARD (
    					CORCARD_NO
    					, CORCARD_NUM
    					, OWNER_NO
    					, CORCARD_START
    					, CORCARD_END
    					, CORCARD_COMP
    					, CORCARD_STATUS
    					, BANK_NAME
    					, M_LIMIT
    					, D_LIMIT
    					, USER_NO
					 ) 
					VALUES (
   						  CORCARD_NO_SEQ.NEXTVAL
   						  , #{corcardNum}
   						  , #{ownerNo}
   						  , #{corcardStart}
   						  , #{corcardEnd}
   						  , #{corcardComp}
   						  , #{corcardStatus}
   						  , #{bankName}
   						  , #{mLimit}
   						  , #{dLimit}
   						  , #{userNo}
					)
