mapper.insert:


			SELECT common_no_seq.NEXTVAL
			FROM dual

		INSERT INTO common_Item (
			common_no
			,name
			,reser_start_time
			,reser_end_time
			,date_select
			,common_kind
			,image
			,content
			)
		VALUES (
			#{commonNo}
			,#{name}
			,#{reserStartTime}
       		,#{reserEndTime}
			,SYSDATE
			,#{commonKind}
			,#{image}
			,#{content}
		)

mapper.update:
update common_Item
set	  name = #{name}
	  ,reser_start_time = #{reserStartTime}
	  ,reser_end_time = #{reserEndTime}
	  ,common_kind = #{commonKind}
	  ,image = #{image}
	  ,content = #{content}
where common_no = #{commonNo}
