mapper.insert:


	SELECT reser_no_seq.NEXTVAL
	FROM dual

insert into reser(
     reser_no
    ,common_name
    ,common_kind
    ,reser_date
    ,reser_start_time
    ,reser_end_time
    ,reser_availability
    ,content
    ,user_no
    ,common_no
    ,usage_status
)values(
	 #{reserNo}
	,#{commonName}
	,#{commonKind}
	,#{reserDate}
	,#{reserStartTime}
	,#{reserEndTime}
	,#{reserAvailability}
	,#{content}
	,#{userNo}
	,#{commonNo}
	,#{usageStatus}
)

mapper.update:
update reser
set	 reser_date = #{reserDate}
    ,reser_start_time = #{reserStartTime}
    ,reser_end_time = #{reserEndTime}
    ,content = #{content}
where reser_no = #{reserNo}

update reser
set	   usage_status = 'a2'
WHERE  reser_no = #{reserNo}
